        changed = false;
        /* eslint-enable no-console */
      }
    }
    else {
      try {
        let val = FormioUtils.jsonLogic.apply(this.component.calculateValue, {
          data,
          row: this.data
        });
        changed = this.setValue(val, flags);
      }
      catch (err) {
        /* eslint-disable no-console */
