      attributes.tabindex = this.component.tabindex;
    }

    if (this.component.autofocus) {
      attributes.autofocus = this.component.autofocus;
    }

    return {
      type: 'input',
      component: this.component,
      changeEvent: 'change',
      attr: attributes
    };
