// Duck-punch the setValueByChoice to ensure we compare using _isEqual.
Choices.prototype.setValueByChoice = function(value) {
  if (!this.isTextElement) {
    const choices = this.store.getChoices();
    // If only one value has been passed, convert to array
    const choiceValue = _isArray(value) ? value : [value];

    // Loop through each value and
    choiceValue.forEach((val) => {
      const foundChoice = choices.find((choice) => {
        // Check 'value' property exists and the choice isn't already selected
        return _isEqual(choice.value, val);
      });

      if (foundChoice) {
        if (!foundChoice.selected) {
          this._addItem(
            foundChoice.value,
            foundChoice.label,
            foundChoice.id,
            foundChoice.groupId,
            foundChoice.customProperties,
            foundChoice.placeholder,
            foundChoice.keyCode
          );
        } else if (!this.config.silent) {
          console.warn('Attempting to select choice already selected');
        }
      } else if (!this.config.silent) {
        console.warn('Attempting to select choice that does not exist');
      }
    });
  }
  return this;
};

export class SelectComponent extends BaseComponent {
  constructor(component, options, data) {
    super(component, options, data);

    // Trigger an update.
    this.triggerUpdate = _debounce(this.updateItems.bind(this), 100);

    // Keep track of the select options.
    this.selectOptions = [];

    // See if this should use the template.
    this.useTemplate = (this.component.dataSrc !== 'values') && this.component.template;

    // If this component has been activated.
    this.activated = false;

    // If they wish to refresh on a value, then add that here.
    if (this.component.refreshOn) {
      this.on('change', (event) => {
        if (this.component.refreshOn === 'data') {
          this.refreshItems();
        }
        else if (event.changed && (event.changed.component.key === this.component.refreshOn)) {
          this.refreshItems();
        }
      });
