      if (this.component[prop]) {
        tableClass += 'table-' + prop + ' ';
      }
    });
    let table = this.ce('table', {
      class: tableClass
    });

    // Build the header.
    if (this.component.header && this.component.header.length) {
      let thead = this.ce('thead');
