    const keyCode = event.keyCode;
    let char = '';

    if (65 <= keyCode && keyCode <= 90) {
      char = String.fromCharCode(keyCode);
    } else if (keyCode === 13) {
      char = 'Enter';
    } else if (keyCode === 27) {
      char = 'Esc';
    }

    _each(this.shortcuts, (shortcut) => {
      if (shortcut.ctrl && !ctrl) {
